INSERT INTO `product` (`product_id`, `name`, `price`, `discount`, `thumbnail`, `year`, `engine`, `type`, `fuel`, `category`, `status`, `feature`, `tong_quan`, `ngoai_that`, `noi_that`, `van_hanh`, `an_toan`, `tien_ich`, `thong_so_ky_thuat`, `thu_vien_anh`, `khung_gam`, `created`) VALUES
(3, 'Mazda CX-3 1.5L Deluxe', '649.000.000VNĐ', '629.000.000VNĐ', 'goc-truoc-mazda-cx-3-deluxe.jpg', 2021, 3, 1, 1, 15, 'Xe mới', 1, '<h1>MAZDA CX-3 - KHẲNG ĐỊNH ĐẲNG CẤP</h1>\r\n\r\n<p>MAZDA CX-3 &ndash; Lựa chọn mới trong ph&acirc;n kh&uacute;c SUV đ&ocirc; thị. Mẫu xe l&agrave; sự kết hợp c&acirc;n bằng giữa phong c&aacute;ch thiết năng động của mẫu xe SUV v&agrave; trải nghiệm l&aacute;i th&uacute; vị, linh hoạt của một chiếc Sedan. Sự kết hợp th&uacute; vị n&agrave;y sẽ mang đến n&eacute;t ri&ecirc;ng đặc trưng thể hiện c&aacute; t&iacute;nh v&agrave; phong c&aacute;ch tự tin của người sở hữu.</p>\r\n\r\n<p><img alt=\"\" src=\"/ckfinder/userfiles/images/Mazda-CX3/cx307-1627011383531.jpg\" style=\"height:371px; width:660px\" /></p>\r\n', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:650px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:white; vertical-align:bottom; width:385px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#101010\">Đ&egrave;n chiếu gần</span></span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"background-color:white; vertical-align:bottom; width:385px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#101010\">Halogen</span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:white; vertical-align:bottom; width:385px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#101010\">Đ&egrave;n chiếu xa</span></span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"background-color:white; vertical-align:bottom; width:385px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif