INSERT INTO `product` (`product_id`, `name`, `price`, `discount`, `thumbnail`, `year`, `engine`, `type`, `fuel`, `category`, `status`, `feature`, `tong_quan`, `ngoai_that`, `noi_that`, `van_hanh`, `an_toan`, `tien_ich`, `thong_so_ky_thuat`, `thu_vien_anh`, `khung_gam`, `created`) VALUES
(51, 'Mazda3 2.0L Signature Premium', '849.000.000 VNĐ', '800.000.000 VNĐ', 'image9-1617942955-911-width630height350.jpg', 2021, 2, 1, 1, 18, 'Xe mới', 0, '<h1 style=\"text-align:center\">THẾ HỆ MỚI NHẤT Đ&Atilde; XUẤT HIỆN</h1>\r\n\r\n<p style=\"text-align:center\"><img src=\"/ckfinder/userfiles/images/image-20220207132934-1.jpeg\" style=\"height:320px; width:630px\" /></p>\r\n\r\n<p style=\"text-align:center\">All-New Mazda3 lấy cảm hứng từ mẫu concept nổi tiếng Vision Coupe &ndash; Mẫu xe Concept đẹp nhất thế giới năm 2018. Mazda3 được thiết kế Phong c&aacute;ch &amp; Quyến rũ với c&aacute;c đường n&eacute;t thanh tho&aacute;t v&agrave; sang trọng, khẳng định vẻ đẹp chuẩn mực vượt thời gian.</p>\r\n\r\n<h2 style=\"text-align:center\">THIẾT KẾ ĐẠT ĐẲNG CẤP NGHỆ THUẬT</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"/ckfinder/userfiles/images/image-20220207132941-2.jpeg\" style=\"height:400px; width:630px\" /></p>\r\n\r\n<p style=\"text-align:center\">Kế thừa ng&ocirc;n ngữ thiết kế KODO - Linh hồn của sự chuyển động, bổ sung những gi&aacute; trị tinh hoa của mỹ thuật truyền thống Nhật Bản v&agrave; triết l&yacute; tinh giản &ldquo;Less is more&rdquo;, đội ngũ thiết kế Mazda đ&atilde; n&acirc;ng tầm sản phẩm thế hệ mới đạt đẳng cấp nghệ thuật &ndash; Car As Art. Thiết kế All-New Mazda3 được tinh giản theo phong c&aacute;ch hiện đại, tạo cảm gi&aacute;c phấn kh&iacute;ch v&agrave; sống động qua sự b&oacute;ng s&aacute;ng được thay đổi theo &aacute;nh nh&igrave;n tr&ecirc;n c&aacute;c đường n&eacute;t của chiếc xe, theo thời gian trong ng&agrave;y v&agrave; theo m&ugrave;a trong năm.</p>\r\n\r\n<h2 style=\"text-align:center\">HUMAN CENTRIC PHILOSOPHY</h2>\r\n\r\n<p style=\"text-align:center\">Với triết l&yacute; thiết kế &ldquo;Human Centric - Lấy con người l&agrave;m trung t&acirc;m&rdquo;, All-New Mazda3 với thiết kế b&ecirc;n trong tạo cảm gi&aacute;c hướng trục, tất cả chi tiết đều được bố tr&iacute; xung quanh người xử dụng. V&agrave; mọi chi tiết đều được hướng đến người sử dụng để tăng cường sự kết nối giữa xe v&agrave; người theo tinh thần &quot;tương t&aacute;c ho&agrave;n hảo&quot;</p>\r\n\r\n<h2 style=\"text-align:center\">C&Ocirc;NG NGHỆ AN TO&Agrave;N I-ACTIVSENSE</h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"/ckfinder/userfiles/images/image-20220207132947-3.png\" style=\"height:621px; width:674px\" /></p>\r\n\r\n<p style=\"text-align:center\">Bộ t&iacute;nh năng an to&agrave;n i-Activsense&reg; được n&acirc;ng cấp với c&aacute;c cải tiến an to&agrave;n tinh vi sẽ cảnh b&aacute;o bạn về những mối nguy hiểm để tr&aacute;nh va chạm. Với c&aacute;c t&iacute;nh năng duy nhất c&oacute; trong ph&acirc;n kh&uacute;c C như: điều khiển h&agrave;nh tr&igrave;nh t&iacute;ch hợp radar (MRCC), hệ thống cảnh b&aacute;o điểm m&ugrave; (BSM), kiểm so&aacute;t v&agrave; giữ l&agrave;n đường (LDWS - LAS), cảnh b&aacute;o phương tiện cắt ngang khi l&ugrave;i (RCTA) v&agrave; nhiều t&iacute;nh năng kh&aacute;c. Những t&iacute;nh năng gi&uacute;p bạn cải thiện tầm nh&igrave;n, hiểu r&otilde; mặt đường v&agrave; kiểm so&aacute;t t&igrave;nh trạng giao th&ocirc;ng xung quanh m&igrave;nh.</p>\r\n', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:650px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:white; vertical-align:bottom; width:385px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"color:#101010\">Đ&egrave;n chiếu gần</span></span></strong></span></span></p>\r\n			</td>\r\n			<td style=\"background-color:white; vertical-align:bottom; width:385px\">\r\n			<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"color:#101010\">LED</span></span></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:white; vertical-align:bottom; width:385px\">\r\n			<p><span style=\